mapper.select:

	select @RNUM := @RNUM +1 AS RNUM, t.*
	from (  
		 select * from bardciel.media ORDER BY #{emotiontype} DESC 
	
	) t, 
	(select @RNUM := 0) R



	select @RNUM := @RNUM +1 AS RNUM, t.*
	from
 
		  	
					( select * from bardciel.media 
					
						WHERE emotion = #{element.emotion} AND point >= #{element.point}  
					
					)t, 
	
		(select @RNUM := 0) R
